import styled from "styled-components"

export const Container = styled.footer `

    width: 100%;
    height: fit-content;
    position: absolute;
    bottom: 0;
    display: flex;
    justify-content: center;
    background-color: lightgrey;


    .conteudo-footer{
        width: 100%
        display: flex;
        margin: auto 0 auto 0;

    }

    img {
        width: 1.8rem;
        height: 1.8rem;
        border-radius: 50%;
        margin-right: 0.5rem;
    }

    span {
        margin: auto 0 auto 0.5rem;
    }

`;
