import styled from "styled-components"

export const Container = styled.footer `

    width: 100%;
    height: fit-content;
    position: fixed;
    bottom: 0;
    display: flex;
    justify-content: center;
    background-color: rgba(0,0,0,0.4);
    align-items: center;
    z-index: 1;

    span {
        color: white;
    }


    div {
        width: 100%;
        display: flex;
        justify-content: center;
        margin: auto 0 auto 0;


    }

    img {
        width: 1.8rem;
        height: 1.8rem;
        border-radius: 50%;
        margin-right: 0.5rem;
    }
`;
