import styled from "styled-components"

export const Background = styled.section`
position: relative;
display: flex;
justify-content: center;
align-items: center;
height: 100vh;
width: 100%;
box-sizing: border-box;
background-color: #B8CDE5;
background-size: cover;

    .titulo {
        background-color: #1C023E;
        width: 30rem;
        height:3rem;
    }

    .titulo h1 {
        color: white;

    }
`;
