background-color: #B8CDE5;
background-size: cover;


    .container {
        display: flex;
        flex-direction: column;
        justify-content: space-around;
        align-items: center;
    }

    .titulo {
        background-color: #1C023E;
        width: 45rem;
        height: 3rem;
        border-radius: 15px;
        display: flex;
