        margin-bottom: 0.3rem;
    }

    .titulo h1 {
        color: white;
        font-size: 2.2rem;
        font-weight: 300;
    }

    .rodizio {
        width: 45rem;
        height: 28rem;
        background-image: url(${Rodizio});
        border-radius: 15px;
        background-size: cover;
        margin-bottom: 0.3rem;
    }

    .terças-em-dobro {
        width: 45rem;
        height: 28rem;
        background-image: url(${Terça_em_dobro});
