import styled from "styled-components"


export const Container = styled.div`
   margin: 0.5rem;
   padding:0;

   input {
       width: ${props => props.batata === "Login" ? "15rem": "10rem" };
       height: ${props => props.batata === "Login" ? "2rem": "1.8rem" };
   }
`;
