import styled from "styled-components"

export const Container = styled.nav`
    width: 100%;
    height: 3.5rem;
    background-color: #1C023E;
    display: flex;
    justify-content: space-between;
    vertical-align: center;



    a{
        text-decoration: none;
        font-size: 1.1rem;
        color: white;

    }

    a:hover {
        cursor: pointer;
        font-size: 1.2rem;
        font-weight: bold;
    }

    div {
       margin: auto 1rem auto 1rem;

    }

    .links {
        display: flex;
        justify-content: space arround;
        width: 30rem;

    }

    .login-img {
        width: 2.5rem;
        height: 2rem;

    }

    .logo-img {
        width: 2.25rem;
        height: 2.25rem;
        border-radius: 50%;

    }
`;
